package JavaBasic;

public class java0_print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		출력하기
//		%d 정수 넣는법
//		%s 문자열
//		%n 줄바꿈
		
		int num = 10;
		
		System.out.printf("줄바꿈 없이 출력");
		System.out.printf("%d", num);
		
		System.out.println();
		System.out.println("자동으로 줄바꿈이 일어남");
		System.out.println(num);
	}

}
